import Cors from 'micro-cors';
import connectDB from '../../db/conexion';
import Users from '../../db/models';

connectDB();

const cors = Cors({
  allowMethods: ['GET', 'HEAD']
})

const user = {
  name: 'Mike',
  email: 'mike@json.com',
}

const handler = (req, res) => {
    res.status(200).json(user);
};

export default cors(handler)
