import Layout from '../components/MyLayout';
import Head from 'next/head';
import fetch from 'isomorphic-unfetch';
import axios from 'axios';

const CustomServer = ({ data }) => {
  return (
    <>
      <Head>
        <title>Custom server</title>
        <meta name="description" content="this is a tv route" />
      </Head>
      <Layout>
        <div>
          <h2>Custom Server</h2>
          <table border="1">
            <thead>
              <tr>
                <th>USERS</th>
                <th>@</th>
              </tr>
            </thead>
            <tbody>
              {data.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button>add user</button>
          <style jsx>
            {`
              table {
                width: 50%;
                margin: 2rem auto;
                border: none;
              }
              thead {
                background: #000;
                color: #fff;
              }
              tbody {
                margin-top: 10px;
                background: #fefefe;
                color: #333;
              }
              th,
              td {
                border-radius: 5px;
                padding: 0.5rem 1rem;
                border: none;
                box-shadow: 0 0 7px rgba(0,0,0,.5);
              }
              button {
                margin-top: 2rem;
                padding: 0.5rem 1rem;
                background: black;
                border: none;
                color: white;
              }
            `}
          </style>
        </div>
      </Layout>
    </>
  );
};

CustomServer.getInitialProps = async (ctx) => {
  const url = `${process.env.URL}/auth`;
  const response = await fetch(url);
  const data = await response.json();

  return { data };
};

export default CustomServer;
