import { useRouter } from 'next/router';
import Layout from '../../components/MyLayout';

const Content = () => {
  const router = useRouter();
  console.log(router);

  return (
    <>
      <h1>{router.query.id}</h1>
      <p>This is the blog post content.</p>
    </>
  );
};

const Post = () => {
  return <Layout>
    <Content />
  </Layout>;
};

export default Post;
