import { useRouter } from 'next/router';
import Layout from '../../components/MyLayout';

const Post = ({ show }) => {
  const router = useRouter();

  return (
    <Layout>
      <h2>{show.name}</h2>
      <p>{show.summary.replace(/<[/]?[pg]>/g, '')}</p>
      {show.image ? <img src={show.image.medium} /> : null}
    </Layout>
  );
};

export default Post;
