import Link from 'next/link';
import Layout from '../components/MyLayout';


const PostLink = ({ id, title }) => (
  <li>
    <Link href="/post/[id]" as={`/post/${id}`}>
      <a>{title}</a>
    </Link>
    <style jsx>{`
      a {
        color: #99ffcc;
      }
    `}</style>
  </li>
);

export default function Blog() {
  const [state, setState] = React.useState([
    { id: 'hello-next', title: 'Hello Next.js' },
    { id: 'learn-next', title: 'Learn Next.js is awesome' },
    { id: 'deploy-next', title: 'Deploy apps with Zeit' }
  ]);

  const [link, setLink] = useState('');

  return (
    <>
      <Layout>
        <h2>My Blog</h2>
        <form onSubmit={() => {setState(link)}}>
          <input type="text" placeholder="value..." value={link} onChange={() => setLink(link)} />
        </form>
        <ul>
          {state.map(post => (
            <PostLink key={post.id} id={post.id} title={post.title} />
          ))}
        </ul>
        <style jsx global>{`
          h2 {
            font-size: 1.2rem;
            text-transform: uppercase;
          }
        `}</style>
        <section className="quote"></section>
      </Layout>
    </>
  );
}
