import Layout from '../components/MyLayout';
import Head from 'next/head';
import fetch from 'isomorphic-unfetch';
import axios from 'axios'

const CustomServer = ({ data }) => {
  return (
    <>
      <Head>
        <title>Custom server</title>
        <meta name="description" content="this is a tv route" />
      </Head>
      <Layout>
        <div>
          <h2>Custom Server</h2>
          <table border='1'>
            <thead>
              <th>USERS</th>
              <th>@</th>
            </thead>
            {data.map((user) => (
              <tbody key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
              </tbody>
            ))}
          </table>
          <button>add user</button>
          <style jsx>
            {`
              table {
                width: 50%;
                margin: 2rem auto;
                border: none;
              }
              thead {
                background: #000;
                color: #fff;

              }
              tbody {
                margin: 10px;
                background: #fefefe;
                color: #333; 
              }
              th, td {
                border-radius: 5px;
                padding: .5rem 1rem;
                border: none;
              }
              button {
                margin-top: 2rem;
                padding: .5rem 1rem;
                background: black;
                border: none;
                color: white;
              }
            `}
          </style>
        </div>
      </Layout>
    </>
  );
};

CustomServer.getInitialProps = async (ctx) => {
  const url = `${process.env.URL}/auth`;
  const response = await fetch(url);
  const data = await response.json();

  return { data };
};

export default CustomServer;
