import Cors from 'micro-cors';

const cors = Cors({
  allowMethods: ['GET', 'HEAD']
});

const handler = (req, res) => {

    try {
      const user = { name: 'mike'}
      res.status(200).json(user);
    } catch (error) {
      console.log(error);
      res.status(500).json({ message: 'error de servidor'});
    }
};

export default cors(handler);
