import Layout from '../components/MyLayout';
import Head from 'next/head';
import fetch from 'isomorphic-unfetch';
import axios from 'axios';
import { useState } from 'react';

const CustomServer = ({ data }) => {

  const [state, setState] = useState({
    name: '',
    email: ''
  });

  function handleSubmit(e) {

  }

  function handleChange(e) {
    setState({ [e.target.name]: e.target.name});
  } 

  return (
    <>
      <Head>
        <title>Custom server</title>
        <meta name="description" content="this is a tv route" />
      </Head>
      <Layout>
        <div>
          <h2>Custom Server</h2>
          <table border="1">
            <thead>
              <tr>
                <th>USERS</th>
                <th>@</th>
              </tr>
            </thead>
            <tbody>
              {data.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name: </label>
            <input type="text" id="name" value={state.name} onChange={handleChange} />
            <label htmlFor="email">Email: </label>
            <input type="email" id="email" value={state.email} onChange={handleChange}/>
            <button type="submit">Save data</button>
          </form>
          <style jsx>
            {`
              table {
                width: 50%;
                margin: 2rem auto;
                border: none;
              }
              thead {
                background: #000;
                color: #fff;
              }
              tbody {
                margin-top: 10px;
                background: #fff0cc;
                color: #333;
              }
              th,
              td {
                border-radius: 2px;
                padding: 0.5rem 1rem;
                border: none;
                box-shadow: 0 0 7px rgba(0,0,0,.3);
              }
              button {
                margin-top: 2rem;
                padding: 0.5rem 1rem;
                background: black;
                border: none;
                color: white;
              }
            `}
          </style>
        </div>
      </Layout>
    </>
  );
};

CustomServer.getInitialProps = async (ctx) => {
  const url = `${process.env.URL}/auth`;
  const response = await fetch(url);
  const data = await response.json();

  return { data };
};

export default CustomServer;
