import Layout from '../components/MyLayout';
import Head from 'next/head';
import fetch from 'isomorphic-unfetch';
import axios from 'axios';
import { useState } from 'react';

const CustomServer = ({ data }) => {
  const [state, setState] = useState({
    msg: null,
    name: '',
    email: '',
  });


  const fetchData = async () => {
    const { name, email } = state;
    if(!email || !name) return
    const response = await axios.post('/auth', {name, email});
    setState({ msg: response.data.message});
  };

  function handleSubmit(e) {
    e.preventDefault();
    fetchData();
  }

  function handleChange(e) {
    setState({ [e.target.name]: e.target.value });
  }

  return (
    <>
      <Head>
        <title>Custom server</title>
        <meta name="description" content="this is a tv route" />
      </Head>
      <Layout>
        <div>
          <h2>Custom Server</h2>
          <main>
            <form onSubmit={handleSubmit}>
              <label htmlFor="name">Name: </label>
              <input
                type="text"
                id="name"
                name="name"
                value={state.name}
                onChange={handleChange}
              />
              <label htmlFor="email">Email: </label>
              <input
                type="email"
                id="email"
                name="email"
                value={state.email}
                onChange={handleChange}
              />
              <button type="submit">Save data</button>
              {state.msg && <smal>{state.msg}</smal>}
            </form>
            <table border="1">
              <thead>
                <tr>
                  <th>USERS</th>
                  <th>@</th>
                </tr>
              </thead>
              <tbody>
                {data.map((user) => (
                  <tr key={user._id}>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </main>
          <style jsx>
            {`
              main {
                display: flex;
              }
              @media (max-width: 720px) {
                main {
                  flex-direction: column;
                }
              }
              table {
                width: 100%;
                margin: 2rem auto;
                border: none;
              }
              thead {
                background: #000;
                color: #fff;
              }
              tbody {
                background: #fff0cc;
                color: #333;
              }
              th,
              td {
                border-radius: 2px;
                padding: 0.5rem 1rem;
                border: none;
                box-shadow: 0 0 7px rgba(0, 0, 0, 0.3);
              }
              form {
                width: 100%;
                margin: 2rem auto;
                background: #eee;
                padding: 2rem;
              }
              input {
                width: 100%;
                padding: 0.5rem 1rem;
                border: none;
                border-radiuds: 2px;
              }
              small {
                color: bluelight;
                font-size: 1rem;
              }
              button {
                margin-top: 2rem;
                padding: 0.5rem 1rem;
                background: black;
                border: none;
                color: white;
              }
            `}
          </style>
        </div>
      </Layout>
    </>
  );
};

CustomServer.getInitialProps = async (ctx) => {
  const url = `${process.env.URL}/auth`;
  const response = await fetch(url);
  const data = await response.json();

  return { data };
};

export default CustomServer;
