import allQuotes from '../../quotes.json';
import Cors from 'micro-cors';

const cors = Cors({
  allowMethods: ['POST', 'BODY'],
});

const handler = (req, res) => {
    res.status(200).json({data: req.body});
};

export default cors(handler);
